import React, { useState, useEffect, useRef } from 'react';
import './module.css';

function ParentComponent({ subModules }) {
    const [activeDropdown, setActiveDropdown] = useState(null);

    const handleDropdownClick = (index) => {
        setActiveDropdown((prevIndex) => (prevIndex === index ? null : index));
    };

    return (
        <div>
            {subModules.map((subMod, index) => (
                <ModuleEntry
                    key={`module-${index}`}
                    header={subMod.header}
                    content={subMod.content}
                    subModules={subMod.subModules}
                    dropdownOpen={activeDropdown === index}
                    onDropdownClick={() => handleDropdownClick(index)}
                />
            ))}
        </div>
    );
}


function ModuleEntry({ header, content, subModules, dropdownOpen, onDropdownClick }) {
    return (
        <div className="module-entry">
            <div className="module-peek">
                <h3>{header}</h3>
                <p>{content}</p>
                <button onClick={onDropdownClick}>Learn more</button>
            </div>
            <div className={`dropdown ${dropdownOpen ? 'open' : 'closed'}`}>
                {subModules.map((subMod, index) => (
                    <div className="subModule" key={`submodule-${index}`}>
                        <h4>{subMod}</h4>
                        <button>Start Lesson</button>
                    </div>
                ))}
            </div>
        </div>
    );
}




export default ModuleEntry;
