import React, { useState, useEffect, useRef } from 'react';
import './module.css';

function ModuleEntry({ header, content }) {
    const [dropdownOpen, setDropdownOpen] = useState(false);
    const [isAnimating, setIsAnimating] = useState(false);

    const handleDropdownClick = () => {
        setDropdownOpen(!dropdownOpen);
        setIsAnimating(true);
    };

    const handleAnimationEnd = () => {
        if (!dropdownOpen) {
            setIsAnimating(false);
        }
    };

    return (
        <div className="module-entry">
            <div className="module-peek">
                <h3>{header}</h3>
                <p>{content}</p>
                <button onClick={handleDropdownClick}>Learn more</button>
            </div>
            <div
                className={`dropdown ${dropdownOpen ? 'open' : 'closed'} ${isAnimating ? 'animating' : 'closed'}`}
                onAnimationEnd={handleAnimationEnd}
            >
                <div className="subModule">
                    <h4>Definition of financial literacy</h4>
                    <button>Start Lesson</button>
                </div>
                <div className="subModule">
                    <h4>Importance of financial literacy</h4>
                    <button>Start Lesson</button>
                </div>
                <div className="subModule">
                    <h4>Overview of the course content</h4>
                    <button>Start Lesson</button>
                </div>
            </div>
        </div>
    );
}



export default ModuleEntry;
