import React, { useState, useEffect, useRef } from 'react';
import './module.css';

function ModuleEntry({ header, content }) {
    const [showDropdown, setShowDropdown] = useState(false);

    const toggleDropdown = () => {
        setShowDropdown(!showDropdown);
    };

    return (
        <div className="module-entry">
            <div className="module-peek">
                <h3>{header}</h3>
                <p>{content}</p>
                <button onClick={toggleDropdown}>Learn more</button>
            </div>
            {showDropdown &&
                    <div className="module-dropdown">
                        <div className="subModule">
                            <h4>Definition of financial literacy</h4>
                            <button>Start Lesson</button>
                        </div>
                        <div className="subModule">
                            <h4>Importance of financial literacy</h4>
                            <button>Start Lesson</button>
                        </div>
                        <div className="subModule">
                            <h4>Overview of the course content</h4>
                            <button>Start Lesson</button>
                        </div>
                    </div>
                }
        </div>
    );
}


export default ModuleEntry;
