import React, { useState, useEffect, useRef } from 'react';


function useCalculateTotalHeight(selector, activeDropdown) {
    const [totalHeight, setTotalHeight] = useState(0);

    console.log(typeof(activeDropdown));
    useEffect(() => {
        function calculateTotalHeight() {
            const allDivs = document.querySelectorAll(selector);
            let heightSum = 0;
            allDivs.forEach((div) => {
                if (activeDropdown != "false") {
                    console.log('here');
                    heightSum += div.querySelector({selector}).offsetHeight + div.querySelector(".dropdown").offsetHeight;
                } else {
                    heightSum += div.querySelector({selector}).offsetHeight;
                }
                heightSum += 2;
            });
            setTotalHeight(heightSum);
        }

        calculateTotalHeight();

        window.addEventListener("resize", calculateTotalHeight);

        return () => {
            window.removeEventListener("resize", calculateTotalHeight);
        };
    }, [activeDropdown, selector]);

    useEffect(() => {
        const styleElement = document.createElement("style");
        styleElement.innerHTML = `.background-height { height: ${totalHeight}px; }`;
        document.head.appendChild(styleElement);

        return () => {
            document.head.removeChild(styleElement);
        };
    }, [totalHeight]);

    return totalHeight;
}


export default useCalculateTotalHeight;
